import React from "react";
import {GetOnePurchase} from "../contexts/provider";
import "../styles/PurchasePage.css"


function PurchasePage(){
    const params = useParams();
    const buyID = params.id;
    
    const sell = GetOnePurchase(localStorage.getItem('user_id'))

    return (
        <div>
            <div className="bread">
                <a href={`/`}>Главная/</a>
                <a href = {'/purchase'}>Заказы</a>
            </div>
            <div className="purchases_block">
                {sell.map(sell =>
                    <div key = {sell.id} className="">
                        <h1>Заказ №{sell.id}</h1>
                        <div>Статус заказа:
                            {
                                sell.status == 1 ?
                                    <div>в ожидании</div>
                                    :
                                    <div>нет информации</div>
                            }
                        </div>
                    </div>
                )}
            </div>
        </div>
    )};
export default PurchasePage;
