import React, {useContext, useState} from "react"
import "../styles/Navigarion.css"
import logo from "../OZON_2019.svg";
import {BACKEND_URL} from "../contexts/provider";

function NavigationBar () {
    const unauthorise = () =>{

        fetch(`${BACKEND_URL}/rest-auth/logout/`, {
            method: "post",
            headers: {
                "Authorization": `Token ${sessionStorage.getItem('token')}`,
                "Content-Type": "application/json"
            },
        })
            .then(res => res.json())
            .then(res => {
                console.log(res);
                sessionStorage.removeItem('token')
                localStorage.removeItem('user_id')
                localStorage.removeItem('user_login')
                window.location.replace("http://127.0.0.1:3000/login");
            })
            .catch(function (reason) {
                console.log(reason);
            })
    }

    return (
        <div className="navigation">
            <div className="nav_block">
                <a className="nav_logo" href="/">
                    <img className="logo" src={logo}/>
                </a>

                <div className="nav_home">
                    <a className="" aria-current="page" href="/">Главная</a>
                </div>

                <div className="nav_product">
                    <a className="" href="/products">Товары</a>
                </div>

                <div className="nav_cart">
                    <a className={`buy_button ${localStorage.getItem('theme')}`} href="/cart">Корзина</a>
                </div>

                <div className={"nav_log_in"}>
                    <input id="buy_button2" className={`buy_button2 ${localStorage.getItem('theme')}`} type="submit" value="Войти" onClick={authorization}/>
                    <input id="buy_button3" className={`buy_button3 ${localStorage.getItem('theme')}`} type="submit" value="Выйти" onClick={authorization}/>
                </div>

                <div className={"nav_user"}>
                    <div className={`buy_button3 ${localStorage.getItem('theme')}`}>Пользователь: {user}</div>
                </div>
            </div>
        </div>
    );
}

export default NavigationBar;
