import React from "react";
import {GetBuys, GetOnePurchase} from "../contexts/provider";
import "../styles/PurchasePage.css";
import {BACKEND_URL} from "../contexts/provider";
import {Link, useParams} from "react-router-dom";


function PurchasePage(){
    const params = useParams();
    const buyID = params.id;
    const buys = GetBuys();
    for (const [key, value] of Object.entries(buys)) {
        if (value.sell != buyID) {
            delete buys[key];
        }
    }

    return (
        <div>
            <div className="bread">
                <a href={`/`}>Главная/</a>
                <a href = {'/purchase'}>Заказы</a>
            </div>

            {GetOnePurchase(localStorage.getItem('user_id')).map(purchase =>
                <div key = {purchase.id} className="manga_block">
                    <h1>Заказ №{purchase.id}</h1>
                    <div>Создан: {purchase.sell_date.split('T')[0]}</div>
                    <div className="in_line_status">Статус заказа: &nbsp;
                        {
                            purchase.status == 1 ?
                                <div className="in_line_status">в ожидании</div>
                                :
                                purchase.status == 2 ?
                                    <div className="in_line_status">отклонён</div>
                                    :
                                    purchase.status == 3 ?
                                        <div className="in_line_status">принят</div>
                                        :
                                        <div className="in_line_status">нет информации</div>
                        }
                    </div>
                    <div>{purchase.comment}</div>
                    <Link className="purchase_link" to={`/models/purchase/${purchase.id}`}>К заказу</Link>
                </div>)}

            {/*{buys.map(buy=>(*/}
            {/*    <div key = {buy.id} className="manga_block3">*/}
            {/*        <div className="image_wrapper">*/}
            {/*            <Link to={`/product/${buy.model}`}>*/}
            {/*                <img className="manga_image" src={`${BACKEND_URL}/media/${buy.product_pic}`} />*/}
            {/*            </Link>*/}
            {/*        </div>*/}
            {/*        <div className="manga_link" >*/}
            {/*            <div>{buy.product_name}</div>*/}
            {/*            <div>Цена: {buy.product_price * buy.quantity} ₽</div>*/}
            {/*            <div>Количество: {buy.quantity} шт</div>*/}
            {/*        </div>*/}
            {/*    </div>*/}
            {/*))}*/}
        </div>
    )
};

export default PurchasePage;
