import { BrowserRouter, Route, Link, Switch } from "react-router-dom";
import Click from "./components/Clicker";
import HomePage from "./components/HomePage";
import AllProductsPage from "./components/AllProductsPage";
import ProductPage from "./components/ProductPage";
import "./App.css";
import { ProductsProvider } from "./contexts";
import React from "react";
import {useEffect, useState} from "react";
import axios from "axios";

// const BACKEND_URL = "http://127.0.0.1:8000";

// export const axiosInstance = axios.create({baseURL: BACKEND_URL})

function App() {
    // const [products, setProducts] = useState([]);
    //
    // useEffect(() => {
    //     fetchAllProducts();
    // }, []);
    //
    // const fetchAllProducts = () => {
    //     axiosInstance
    //         .get('/product')
    //         .then(response => setProducts(response.data))
    // }

    return (
        <div className="App">
            <BrowserRouter basename="/" >
                <div>
                    <ul>
                        <li><Link to="/">Старт</Link></li>
                        <li><Link to="/products">Каталог</Link></li>
                        <li><Link to="/click">Клик-клак</Link></li>
                    </ul>
                    <hr />
                    <Switch>
                        <Route exact path="/"><HomePage /></Route>

                        <Route path="/products">
                            <ProductsProvider>
                                <AllProductsPage />
                            </ProductsProvider>
                        </Route>

                        <Route path="/products/:id">
                            <ProductsProvider>
                                <ProductPage />
                            </ProductsProvider>
                        </Route>

                        <Route path="/click"><Click /></Route>
                    </Switch>
                </div>
            </BrowserRouter>
        </div>
    );
}

export default App;
