import { BrowserRouter, Route, Link, Switch } from "react-router-dom";
import Click from "./Clicker";
import Home from "./HomePage";
import Catalog from "./Catalog";
import {ProductPage} from "./components/ProductPage";
// import {products_data} from "./ProductsDict";
import "./App.css";
import {useEffect, useState} from "react";
import ProductList from "./components/productList";
import axios from "axios";

const BACKEND_URL = "http://127.0.0.1:8000";

export const axiosInstance = axios.create({baseURL: BACKEND_URL})

function App() {
    const [products, setProducts] = useState([]);

    useEffect(() => {
        fetchAllProducts();
    }, []);

    const fetchAllProducts = () => {
        axiosInstance
            .get('/product')
            .then(response => setProducts(response.data))
    }

    return (
        <div className="App">
            <BrowserRouter basename="/" >
                <div>
                    <ul>
                        <li><Link to="/">Старт</Link></li>
                        <li><Link to="/catalog">Каталог</Link></li>
                        <li><Link to="/click">Клик-клак</Link></li>
                    </ul>
                    <hr />
                    <Switch>
                        <Route exact path="/"><Home /></Route>
                        <Route path="/catalog"><ProductList data={products} /></Route>
                        <Route path="/click"><Click /></Route>
                        <Route path="/product/:id"><ProductPage data={products}></ProductPage></Route>
                    </Switch>
                </div>
            </BrowserRouter>
        </div>
    );
}

export default App;
