import React from "react";
import {GetOnePurchase, GetBuys, GetPurchases} from "../contexts/provider";
import "../styles/PurchasePage.css"
import {Link, useParams} from "react-router-dom";
import {BACKEND_URL} from "../contexts/provider";


function PurchasePage() {
    const userID = localStorage.getItem('user_id');
    const sell = GetBuys();
    for (const [key, value] of Object.entries(buys)) {
        if (value.user != userID) {
            delete buys[key];
        }
    }

    return (
        <div>
            <div className="bread">
                <a href={`/`}>Главная/</a>
                <a href = {'/purchase'}>Заказы</a>
            </div>
            <div className="purchases_block">
                {buys.map(buy =>
                    <div key = {buy.id} className="single_purchase_block">
                        <h1>Заказ №{buy.sell}</h1>
                        <div className="in_line_status">Статус заказа: &nbsp;
                            {
                                buy.status == 1 ?
                                    <div className="in_line_status">в ожидании</div>
                                    :
                                    buy.status == 0 ?
                                        <div className="in_line_status">удалён</div>
                                        :
                                        buy.status == 2 ?
                                            <div className="in_line_status">отклонён</div>
                                            :
                                            buy.status == 3 ?
                                                <div className="in_line_status">принят</div>
                                                :
                                                buy.status == 4 ?
                                                    <div className="in_line_status">завершён</div>
                                                    :
                                                    <div className="in_line_status">нет информации</div>
                            }
                        </div>
                        <div className="in_line_info_block">
                            <div className="cart_image_block">
                                <Link to={`/product/${buy.product}`}>
                                    <img className="cart_image" src={`${BACKEND_URL}/media/${buy.product_pic}`} />
                                </Link>
                            </div>
                            <div>{buy.product_name}</div>
                            <div>Цена: {buy.product_price * buy.quantity} ₽</div>
                            <div>Количество: {buy.quantity} шт.</div>
                        </div>
                    </div>
                )}
            </div>
        </div>
    )};
export default PurchasePage;
