import {GetBuys} from "../contexts/provider";
import React, {useState} from "react";
import {Link, useParams} from "react-router-dom";
import "../styles/ManagerPage.css"
import {BACKEND_URL} from "../contexts/provider";

function ProductManagingPage() {

    const params = useParams();
    const buyId = params.id
    console.log(params)
    const buys = GetBuys()
    let id_user, sell_date, order_status
    for (const [key, value] of Object.entries(buys)) {
        if (value.sell != buyId) {
            delete buys[key];
        } else {
            id_user = value.user
            sell_date = value.sell_date
            order_status = value.status
        }
    }

    function accept_order() {
        const ob = {
            id: buyId,
            user: id_user,
            sell_date: sell_date,
            status: 3
        }
        fetch(`${BACKEND_URL}/sell/${buyId}/`, {
            method: "put",
            headers: {
                "Authorization": `Token ${sessionStorage.getItem('token')}`,
                "Content-Type": "application/json"
            },
            body: JSON.stringify(ob),
        })
            .catch(function (reason) {
                alert('Неправильный!')
            })
    }

    function reject_order() {
        const ob = {
            id: buyId,
            user: id_user,
            sell_date: sell_date,
            status: 2
        }
        fetch(`${BACKEND_URL}/sell/${buyId}/`, {
            method: "put",
            headers: {
                "Authorization": `Token ${sessionStorage.getItem('token')}`,
                "Content-Type": "application/json"
            },
            body: JSON.stringify(ob),
        })
            .catch(function (reason) {
                alert('Неправильный!')
            })
    }

    return(
        <div>
            <a href={`/`}>Начало/</a>
            <a href={'/managers'}>Заказы/</a>
            <a href={`/manager/${buyId}`}>Заказ №{buyId}</a>
            <br />
            <div>
                <h2>Заказ: №{buyId}</h2>
                {
                    order_status == 1 ?
                        <h3>Статус: в ожидании</h3>
                        :
                        order_status == 2 ?
                            <h3>Статус: доставляется</h3>
                            :
                            order_status == 3 ?
                                <h3>Статус: отклонен</h3>
                                :
                                order_status == 4 ?
                                    <h3>Статус: отменен</h3>
                                    :
                                    <h3>Статус: доставлен</h3>

                }

                {sells.status == 1 || sells.status == 2 ?
                    <div key={sells.id} className="m_purchase_row">
                        <div className={"purchase"}>Заказ №{sells.id}</div>
                        <div>Статус заказа:
                            {
                                sells.status == 1 ?
                                    <div className={"status"}>в ожидании</div>
                                    : sells.status == 2 ?
                                        <div className={"status"}>в доставке</div>
                                        : sells.status == 3 ?
                                            <div className={"status"}>отклонен</div>
                                            :
                                            sells.status == 4 ?
                                                <div className={"status"}>отменен</div>
                                                :
                                                <div className={"status"}>доставлен</div>
                            }
                        </div>
                        <div className={"purchase_date"}>Дата последнего изменения: {sells.sell_date.split('.')[0].replace('T', ' ')}</div>
                        <div className={"m_purchase_block"}>
                            {sell.map(sell =>
                                <div>
                                    {
                                        sells.id == sell.sell ?
                                            <div key={sell.sell} className="m_purchase_line">
                                                <div className={"m_purchase_title_block"}>{sell.title}</div>
                                                <div
                                                    className={"m_purchase_quantity_block"}>Количество: {sell.quantity} шт
                                                </div>
                                                <div
                                                    className={"m_purchase_price_block"}>Цена: {sell.price * sell.quantity} ₽
                                                </div>
                                            </div>
                                            :
                                            <div></div>
                                    }
                                </div>
                            )}
                        </div>
                        <div>
                            {
                                sells.status === 2 ?
                                    <button className="m_purchase_accept_button" onClick={() => {
                                        accept_reject(sells.id, 5)
                                    }}>Доставлен
                                    </button>
                                    :
                                    <button className="m_purchase_accept_button" onClick={() => {
                                        accept_reject(sells.id, 2)
                                    }}>Принять заказ
                                    </button>
                            }
                            {
                                sells.status !== 2 &&
                                <button className="m_purchase_reject_button" onClick={() => {
                                    accept_reject(sells.id, 3)
                                }}>Отклонить заказ
                                </button>
                            }
                        </div>
                    </div>
                    :
                    <div></div>
                }
            </div>
            )}

            {/*    {buys.map(buy => (*/}
            {/*        <div key = {buy.id} className="manga_block3">*/}
            {/*            <div className="image_wrapper">*/}
            {/*                <Link to={`/product/${buy.product}`}>*/}
            {/*                    <img className="single_product_image" src={`${BACKEND_URL}/media/${buy.product_pic}`} />*/}
            {/*                </Link>*/}
            {/*            </div>*/}
            {/*            <div className="manga_link" >*/}
            {/*                <div>{buy.product_name}</div>*/}
            {/*                <div>Цена: {buy.product_price * buy.quantity} ₽</div>*/}
            {/*                <div>Количество: {buy.quantity} шт.</div>*/}
            {/*            </div>*/}
            {/*        </div>*/}
            {/*    ))}*/}
            {/*</div>*/}
            {/*<div className="form-block2">*/}
            {/*    <button*/}
            {/*        className="action-block34"*/}
            {/*        onClick={() => accept_order()}*/}
            {/*    >*/}
            {/*        Принять*/}
            {/*    </button>*/}
            {/*    <button*/}
            {/*        className="action-block34"*/}
            {/*        onClick={() => reject_order()}*/}
            {/*    >*/}
            {/*        Отклонить*/}
            {/*    </button>*/}
            {/*</div>*/}
        </div>
    );

}

export default ProductManagingPage;
