import { useParams } from "react-router-dom";
import {useState, useEffect} from "react";
import {BACKEND_URL, GetOneProduct} from "../contexts/provider";
import "../styles/ProductPage.css"

function ProductPage() {
    const params = useParams();
    const productId = params.id;
    const product = GetOneProduct(productId);

    const buy = (quantity, user, product) => {
        const ob = {
            quantity: quantity,
            id_user: user,
            id_product: product
        }
        fetch(`${BACKEND_URL}/cart/`, {
            method: "post",
            headers: {
                "Content-Type": "application/json"
            },
            body: JSON.stringify(ob)
        })
            .then(res => res.json())
            .then(res => {
                console.log(JSON.stringify(ob));
            })
    }

    const get_user=()=> {
        console.log()
        fetch(`${BACKEND_URL}/rest-auth/user/`, {
            headers: {
                "Authorization": `Token ${sessionStorage.getItem('token')}`,
            },
        })
            .then(response => response.json())
            .then(data => {
                console.log(data.pk)
                setUser(data);
            })
    }

    const [user, setUser] = useState(
        {
            "pk": 1,
            "username": "",
            "email": "",
            "first_name": "",
            "last_name": ""
        }
    )

    useEffect( () => {
        get_user()
    }, [])

    return (
        <div>
            <a href="/" className="breadcrumbs">Главная </a>
            <a href="/products" className="breadcrumbs">- Каталог </a>
            <a href={`/products/${productId}`} className="breadcrumbs">- {product.product_name}</a>
            <div className="product_info">
                <div className="product_name">{product.product_name}</div>
                <img src={product.product_pic}></img>
                <div className="product_price">{product.product_price}₽</div>
                <input id="buy_button"
                       className={`buy_button ${localStorage.getItem('theme')}`}
                       type="submit"
                       value="В корзину"
                       onClick={() => {
                           buy(1, 1, productId);
                           alert("Добавлено в корзину");
                       }}
                />
            </div>
        </div>
    );
};

export default ProductPage;
