import { useParams } from "react-router-dom";
import {BACKEND_URL, GetOneProduct} from "../contexts/provider";
import "../styles/ProductPage.css"

function ProductPage() {
    const params = useParams();
    const productId = params.id;
    const product = GetOneProduct(productId);

    const buy = (quantity, user, product) => {
        const ob = {
            quantity: quantity,
            user: user,
            product: product
        }
        fetch(`${BACKEND_URL}/cart/`, {
            method: "post",
            headers: {
                "Content-Type": "application/json"
            },
            body: JSON.stringify(ob)
        })
            .then(res => res.json())
            .then(res => {
                console.log(res);
            })
    }

    return (
        <div>
            <a href="/" className="breadcrumbs">Главная </a>
            <a href="/products" className="breadcrumbs">- Каталог </a>
            <a href={`/products/${productId}`} className="breadcrumbs">- {product.product_name}</a>
            <div className="product_info">
                <div className="product_name">{product.product_name}</div>
                <img src={product.product_pic}></img>
                <div className="product_price">{product.product_price}₽</div>
                <input id="buy_button" className={`buy_button ${localStorage.getItem('theme')}`} type="submit"
                        value="В корзину" onClick={()=>{buy(1, 1, productId)}}/>
            </div>
        </div>
    );
};

export default ProductPage;
