import React, {useState} from "react";
import {Link} from "react-router-dom";
import '../styles/RegistrationPage.css';
import {BACKEND_URL} from "../contexts/provider";

function RegistrationPage() {
    const [log, setLog] = useState('');
    const [pass, setPass] = useState('');
    const [mail, setMail] = useState('');

    function Create() {
        const ob = {
            username: log,
            email:mail,
            password1: pass,
            password2: pass,
        }
        fetch(`${BACKEND_URL}/rest-auth/registration/`, {
            method: "post",
            headers: {
                "Content-Type": "application/json"
            },
            body: JSON.stringify(ob)
        })
            .then(res => res.json())
            .then(res => {
                console.log(res);
                const token = res.data.key;
                localStorage.setItem('token', token);
            })
            .catch(function (reason) {
                alert("Пароль не менее 8 символов")
                window.location.replace("/registration")
            })
    }


    return (
        <div className="register-block">
            <h1 className="title-block">
                Регистрация
            </h1>
            <form className="form-block">
                <div className="">
                    <label
                        htmlFor="login"
                        className="text-block33"
                    >
                        Логин
                    </label>
                    <input
                        type="login"
                        onChange={(event) => setLog(event.target.value)}
                        value={log}
                        className="input-block33"
                    />
                </div>
                <div className="">
                    <label
                        htmlFor="password"
                        className="text-block33"
                    >
                        Пароль
                    </label>
                    <input
                        type="password"
                        onChange={(event) => setPass(event.target.value)}
                        value={pass}
                        className="input-block33"
                    />
                </div>
                <div className="">
                    <label
                        htmlFor="name"
                        className="text-block33"
                    >
                        Почта
                    </label>
                    <input
                        type="name"
                        onChange={(event) => setMail(event.target.value)}
                        value={mail}
                        className="input-block33"
                    />
                </div>
                <br />
                <div className="">
                    <Link to="/login"
                          className="action-block33"
                          onClick={() => Create()}
                    >
                        Зарегестрироваться
                    </Link>
                </div>
            </form>

            <p className="repage-block">
                {" "}
                Уже есть аккаунт?{" "}
                <a
                    href="/login"
                    className="login"
                >
                    Войти
                </a>
            </p>
        </div>
    )
}
export default RegistrationPage;
